import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'

class Post extends React.Component {
    constructor(props){
        super(props)
    }

    onClickDelete = (event) =>{
      event.preventDefault()
      axios({
        method: "DELETE",
        url: "/api/post/" + this.props.post.id,
        headers : {
            Authorization: "Bearer" + localStorage.getItem("userToken")
        },
      }).then(res => {
        console.log(res)
        window.location.replace('/')
    })
    }

    render(){
      const category = this.props.post.categories ? this.props.post.categories.map(res => {
        return <li><a href={'/tags/'+res.id}>Learn</a></li>
          }) : <></>
        return (
          <div class="blog-card">
            <div class="meta">
              <div class="photo" style={{backgroundImage: 'url(https://storage.googleapis.com/chydlx/codepen/blog-cards/image-1.jpg)'}}></div>
              <ul class="details">
                <li class="author"><a href="#">John Doe</a></li>
                <li class="date">Aug. 24, 2015</li>
                <li class="tags">
                  <ul>
                    {category}
                  </ul>
                </li>
              </ul>
            </div>
            <div class="description">
              <h1>Learning to Code</h1>
              <h2>Opening a door to the future</h2>
              <p> Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ad eum dolorum architecto obcaecati enim dicta praesentium, quam nobis! Neque ad aliquam facilis numquam. Veritatis, sit.</p>
              <p class="read-more">
                <a href="#">Read More</a>
              </p>
            </div>
          </div>
      )
    }
}
  

export default Post;
