import React from 'react'
import axios from 'axios'
import { validateAll } from 'indicative';

class ModalEdit extends React.Component {
    constructor(props){
        super(props)
        this.state={
            errors: [],
            first_name: '',
            last_name: '',
            phone: '',
            facebook: '',
            twitter: '',
            github: '',
            website: '',
            skill: [],
            subject : '',
            status: '',
        }
    }
    handleInputChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value,
          errors: [],
        })
    }

    rules = {
        first_name:  'required|string',
        last_name:'required|string',
        phone:'numeric'
      };
     messages = {
        required: 'The {{ field }} is required.',
        numeric: ' the {{field}} is not numetic',
      }

      handleSubmit = (event) => {
        event.preventDefault()
        const data = {
            first_name : this.state.first_name != '' ? this.state.first_name : this.props.profile.first_name,
            last_name : this.state.last_name != '' ? this.state.last_name : this.props.profile.last_name,
            phone : this.state.phone,
            facebook: this.state.facebook,
            twitter: this.state.twitter,
            github: this.state.github,
            website: this.state.website,
            skill : this.state.skill,
            subject: this.state.subject,
            status : this.state.status,
        };
        console.log(data)
        // validateAll(data,this.rules, this.messages)
        // .then(async () => {
        //   //successfull login
        //   await axios.post('/api/register', {
        //     first_name: this.state.first_name,
        //     last_name:this.state.last_name,
        //     email: this.state.email,
        //     password: this.state.password,
        //     password_confirmation: this.state.password_confirmation,
        //     subject : this.state.subject,
        //   }).then(response =>  {
        //     console.log('b')
        //     if(response.status == 201) {
        //       window.location.replace('/login')
        //     }
            
        //   }).catch(errors => {
        //     console.log(errors.response);
        //     alert(errors.response.data.errors)
        //   })
        // })
        // .catch(errors => {
        //   console.log('a')
        //   console.log(errors);
        //   const formattedErrors = {}
        //   errors.forEach(error => formattedErrors[error.field] = error.message)
        //   this.setState({
        //     errors: formattedErrors
        //   })
        // })
      }

    render(){
        return (
            <>
                <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
                    Edit Profile
                    </button>
                    <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div class="modal-dialog" role="document">
                        <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Edit Your Profile</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                        <form className="form-type-material" onSubmit={this.handleSubmit}>
                            <div className="form-group">
                            <label for="InputPassword">First Name</label>
                            <input type="text" className="form-control" name="first_name" value={this.props.profile.first_name} placeholder="First name" onChange={this.handleInputChange}/>
                            {
                                this.state.errors['first_name'] &&
                                <small className="text-danger">{this.state.errors['first_name']}</small>

                            }
                            </div>
                            <div className="form-group">
                            <label for="InputPassword">Last Name</label>
                            <input type="text" className="form-control" name="last_name" value={this.props.profile.last_name} placeholder="Last name" onChange={this.handleInputChange}/>
                            {
                                this.state.errors['last_name'] &&
                                <small className="text-danger">{this.state.errors['last_name']}</small>
                            }
                            </div>
                            <div className="form-group">
                            <label for="InputPassword">Phone</label>
                            <input type="text" className="form-control" name="phone" value={this.props.profile.phone} placeholder="Phone " onChange={this.handleInputChange}/>
                            {
                                this.state.errors['phone'] &&
                                <small className="text-danger">{this.state.errors['phone']}</small>
                            }
                            </div>
                            <div className="form-group">
                            <label for="InputPassword">Facebook</label>
                            <input type="text" className="form-control" name="facebook" value={this.props.profile.facebook} placeholder="facebook" onChange={this.handleInputChange}/>
                            {
                                this.state.errors['facebook'] &&
                                <small className="text-danger">{this.state.errors['facebook']}</small>
                            }
                            </div>
                            <div className="form-group">
                            <label for="InputPassword">Twitter</label>
                            <input type="text" className="form-control" name="twitter" value={this.props.profile.twitter} placeholder="twitter" onChange={this.handleInputChange}/>
                            {
                                this.state.errors['twitter'] &&
                                <small className="text-danger">{this.state.errors['twitter']}</small>
                            }
                            </div>
                            <div className="form-group">
                            <label for="InputPassword">Github</label>
                            <input type="text" className="form-control" name="github" value={this.props.profile.github} placeholder="github" onChange={this.handleInputChange}/>
                            {
                                this.state.errors['github'] &&
                                <small className="text-danger">{this.state.errors['github']}</small>
                            }
                            </div>
                            <div className="form-group">
                            <label for="InputPassword">Website</label>
                            <input type="text" className="form-control" name="website" value={this.props.profile.website} placeholder="website" onChange={this.handleInputChange}/>
                            {
                                this.state.errors['website'] &&
                                <small className="text-danger">{this.state.errors['website']}</small>
                            }
                            </div>
                            <div class="form-group">
                            <label for="InputPassword">Subject</label>
                            <select class="form-control" name="subject" id="exampleFormControlSelect1" value={this.props.profile.subject} onChange={this.handleInputChange}>
                                <option value="student">student</option>
                                <option value="develop">develop</option>
                                <option value="tester">tester</option>
                                <option value="devOps">devOps</option>
                                <option value="Other">Other</option>
                            </select>
                            </div>
                            

                            <div class="form-group">
                                <label for="exampleFormControlSelect2">Skill</label>
                                <select multiple name="skill[]" class="form-control" id="exampleFormControlSelect2">
                                <option value="1">1</option>
                                <option value="2">2</option>
                                <option value="3">3</option>
                                <option value="4">4</option>
                                <option value="5">5</option>
                                </select>
                            </div>
                            <div class="form-group">
                            <label for="InputPassword">Status</label>
                            <select class="form-control" name="status" id="exampleFormControlSelect1" value={this.props.profile.status} onChange={this.handleInputChange}>
                                <option value="Public">Public</option>
                                <option value="Private">private</option>
                            </select>
                            </div>
                        </form>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary" onClick={this.handleSubmit}>Save changes</button>
                        </div>
                        </div>
                    </div>
                    </div>
            </>
        )
    }
}
export default ModalEdit;
