import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Redirect, Route, Switch, withRouter } from 'react-router-dom';
import Navbar from './components/Navbar';
import Welcome from './components/Welcome';
import Footer from './components/Footer';
import Login from './components/Login';
import SignUp from './components/SignUp/index';
import SingleArticle from './components/SingleArticle';
import CreateArtice from './components/CreateArticle';
import * as serviceWorker from './serviceWorker';
import Profile from './components/Profile/profile';
import "./index.css";
import Form from './components/Profile/form';
import axios from 'axios';
import About from './components/about';
import ForgotPassword from './components/Forgot';
import Page404 from './components/404Page';


const Main = withRouter(({ location }) => {
  const checkLogin= () => {
      axios.get("http://127.0.0.1:8000/api/user/profile",{
            headers: {'Authorization' : 'Bearer ' + localStorage.getItem("userToken")}
        }).then(res => {
            console.log(res)
            if(res.status == 200){
                this.setState({
                    user:res.data.user,
                    profile: res.data.profile,
                    skill : res.data.skill,
                    action: res.data.action
                })
                console.log(this.state.user.name)
            }
        }).catch(errors => {
            console.log(errors.response);
            alert(errors.response.data.errors)
            if(errors.response.status == 401) {
                localStorage.removeItem("userToken")
                localStorage.removeItem("userName")
                window.location.replace('/login')
            }
      })
  }
return (
<div>
  {
    location.pathname !== '/login' && location.pathname !== '/signup' && location.pathname !== '/forgotPassword' &&
    <Navbar/>
  }
    <Switch>
      {localStorage.getItem("userToken") ? 
      <>
        <Route exact path="/" component={localStorage.getItem("userToken") == null ? About : Welcome}/>   
        <Route  exact path="/user/profile" component={Profile}/>
        <Route exact path="/article/:slug" component={SingleArticle}/>
        <Route exact path="/articles/create" component={CreateArtice}/>
        <Route exact path="/user/profile/:id" component={Form} />
      </> :
      <>
        <Route exact path="/signup" component={SignUp} />
        <Route exact path="/login" component={Login}/>
      </>}
      
      <Route exact path="/login" >
        {localStorage.getItem("userToken") ? 
        <>
        <Redirect to="/"/>
        </>
        : <Login></Login>}
      </Route>
      <Route exact path="/signup" >
        {localStorage.getItem("userToken") ? 
        <>
        <Redirect to="/"/>
        </>
        : <SignUp></SignUp>}
      </Route>
      <Route exact path="/forgotPassword" >
        {localStorage.getItem("userToken") ? 
        <>
        <Redirect to="/"/>
        </>
        : <ForgotPassword></ForgotPassword>}
      </Route>
      <Route>
        <Page404 />
      </Route>
    </Switch>
    
    
    {
    location.pathname !== '/login' && location.pathname !== '/signup' && location.pathname !== '/forgotPassword'&&
    <Footer />
    }
  </div>
);
});

ReactDOM.render(
  <BrowserRouter>
  <Main />
  </BrowserRouter>, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
