import React from 'react';
import Banner from './../Banner';

import Post from '../Post/post';
import axios from 'axios';

class  Tags extends React.Component{
  constructor(props){
    super(props)
    this.state={
      categories: [],
    }
  }

  componentDidMount(){
    axios.get("/api/categories",{
        headers : {
            Authorization: "Bearer" + localStorage.getItem("userToken")
        },
    }).then(res =>{
        this.setState({
            categories : res.data.categories
        })
        console.log(this.state.categories)
    }).catch(errors =>{
      alert(errors.response.data.errors)
    })
  }

  render(){
      const RenderPost = this.state.categories !== [] ? 
      this.state.categories.map(res => {
        return (
            <>
            <div className="col-12 col-xl-3">
                <h5>
                    <a href={'/tags/'+res.id}>
                    {res.name}
                </a>
                </h5>
            </div>
            <div className="col-12 col-xl-6">
                Bài viết nổi bật : {res.post[0].title}
            </div>
            <div className="col-12 col-xl-3" >
                hello
            </div>
            <hr></hr>
            </>
        ) 
      })
      : 
      null
    return (
    <div>
      <Banner 
      backgroundImage="url(assets/img/bg-gift.jpg)"
      title="Latest Blog Posts"
      subtitle="Read and get updated on the latest posts"
      />
    <main className="main-content" style={{paddingTop: '5px'}}>
        
      <div className="container">
      <h5>Follow Tags Để nhận thông báo về bài viết mới nhất</h5>
      <br/>
        <div className="row">
            <hr></hr>
          {RenderPost}
        </div>

    </div>
    {/*
|‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒
| Comments
|‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒‒
!*/}
    
  </main>
    </div>
    )
};
}

export default Tags;
