import React from 'react'
import axios from 'axios'

export default class Comments extends React.Component {
    constructor(props){
        super(props)
        this.state={
            comment: []
        }
    }

    componentDidMount(){
        axios.get("/api/post/comments", {
            params:{
                "posr_id" : this.props.post_id,
            }
        }).then(res=>{
            console.log("a")
            console.log(res.data.comments)
            this.setState({
                comment : res.data.comments
            })
        })
    }

    render(){
        return (
                            <div className="card card-white post bg-grey">
                                <div className="post-heading">
                                    <div className="float-left image">
                                        <img src={this.props.data.user.profile.avatar} className="img-circle avatar" alt="user profile image"/>
                                    </div>
                                    <div className="float-left meta">
                                        <div className="title h5">
                                            <a href={"/user/profile/"+this.props.data.user_id}><b>{this.props.data.user.name}</b></a>
                                        </div>
                                        <h6 className="text-muted time">
                                        <date>{new Date(Date.parse(this.props.data.created_at)).toLocaleString()}</date>
                                          
                                            </h6>
                                    </div>
                                </div> 
                                <div className="post-description"> 
                                    <p>{this.props.data.comment}</p>
                                </div>
                                <hr/>
                            </div>
        )
    }
}
