import React from 'react'
import Banner from '../Banner'
import axios from 'axios'
import Post from '../Post/post'

class UserBlog extends React.Component {
    constructor(props){
        super(props)
        this.state={
            post:[],
            user:[],
        }
    }
    
    componentDidMount(){
        axios.get("http://127.0.0.1:8000/api/post/user").then(res =>{
          this.setState({
            post: res.data.user.post,
            user: res.data.user,
          })
        }).catch(errors =>{
          alert(errors.response.data.errors)
        })
      }
    
    render(){
        const RenderPost = this.state.post != [] ? 
            this.state.post.map(res => {
                return (
                    <>
                    <Post post={res}/>
                    </>
                ) 
            })
            : 
            null
        return(
            <div>
            <Banner
            backgroundImage="url(assets/img/bg-gift.jpg)"
            title="Your Blog"
            />
            <main className="main-content bg-gray">
            <div className="row">
                <div className="col-12 col-lg-6 offset-lg-3">
                {RenderPost}
                <nav className="flexbox mt-50 mb-50">
                <a className="btn btn-white disabled">
                    <i className="ti-arrow-left fs-9 mr-4" /> Newer</a>
                <a className="btn btn-white" href="#">Older
                    <i className="ti-arrow-right fs-9 ml-4" />
                </a>
                </nav>
                </div>
            </div>
            </main>

            </div>
            
        )
    }
}
export default UserBlog;
