import React from 'react';
import Select from 'react-select';
import Banner from './../Banner';
import axios from 'axios';

class Createpost extends React.Component {

    constructor(props){
        super(props)
        this.state={
           title:'',
           content:'',
           conclusion:'',
           intro:'',
           author:'',
           cover:'',
           errors:[],
            category: [],
        }
    }

    options = [
        { label: "PHP", value: "1" },
        { label: "Laravel", value: "2" },
        { label: "Nodejs", value: "3" },
        { label: "Angulajs", value: "4" },
        { label: "JS", value: "5" },
        { label: "Java", value: "6" },
        { label: "Python", value: "7" },
      ];

    componentDidMount(){
        axios.get("http://127.0.0.1:8000/api/category").then(res => {
            this.setState({
                category : res.data.category
            })
        })
    }

    onSelectedOptionsChange= (event) => {
        this.setState({
            category : event ? event.map(x => x.value) : []
        })
        console.log(this.state.category)
    }

    onHandleInputChange= event =>{
        
    }

    render(){
        return (
        <div>
        <Banner
        backgroundImage={`url(${process.env.PUBLIC_URL}/assets/img/bg-laptop.jpg)`}
        title="Write an Article"
        />
        {/* END Header */}
        {/* Main container */}
        <main className="main-content">
            <section className="section">
            <div className="container">
                <div className="row">
                <div className="col-12 col-lg-12">
                    <form className="p-30 bg-gray rounded" method="POST" data-form="mailer">
                    <div className="row">
                        <div className="form-group col-md-12 my-5">
                        <input type="file" className="form-control" name="cover" />
                        </div>
                        <div className="form-group col-12 col-md-6">
                        <input className="form-control form-control-lg" type="text" name="title" placeholder="Title" />
                        </div>
                        <div className="form-group col-12 col-md-6">
                        <Select
                                    isMulti
                                    name="category"
                                    options={this.options}
                                    onChange={this.onSelectedOptionsChange}
                                    className="basic-multi-select"
                                    classNamePrefix="Category"
                                    placeholder="Category"
                            />
                        </div>
                    </div>
                    <div className="form-group">
                        <textarea className="form-control form-control-lg" rows={2} placeholder="Intro" name="Intro" defaultValue={""} />
                    </div>
                    <div className="form-group">
                        <textarea className="form-control form-control-lg" rows={4} placeholder="Content" name="Content" defaultValue={""} />
                    </div>
                    <div className="form-group">
                        <textarea className="form-control form-control-lg" rows={2} placeholder="Conclusion" name="Conclusion" defaultValue={""} />
                    </div>
                    <div className="text-center">
                        <button className="btn btn-lg btn-primary" type="submit">Create Post</button>
                    </div>
                    </form>
                </div>
                </div>
            </div>
            </section>
        </main>
        {/* END Main container */}
        </div>

  );
};
}
export default Createpost;
